     "table column name"           => [1, "name of the column"],
     "table column type"           => [1, "type of the column"],
     "table hide"                  => ["-", "Hide this table if present"],
     "table name"                  => [1, "name of the table"],
     "table reference"             => ["*", "References from this table"],
