if (0) {                                                                        # Performance of by
  use Time::HiRes qw(time);
  my $M = 10;
  my $N = 4;
  my $a = sub
   {my $a = qq(<a/>)       x$M;
    my $b = qq(<b>$a</b>\n)x$M;
    my $c = qq(<c>$b</c>\n)x$M;
    my $d = qq(<d>$c</d>\n)x$M;
    my $e = qq(<e>$d</e>\n)x$M;
    my $f = qq(<f>$e</f>);
    Data::Edit::Xml::new($f);
   }->();

  for(1..$N)
   {my @d;
    for my $sub(qw(by By))
     {my $start = time;
      for (1..$N)
       {$a->$sub(sub
         {my ($o) = @_;
         });
       }
      push @d, time - $start;
     }
    say STDERR "AAAA ", $d[1]/$d[0];                                            # By / by
   }
  exit;
 }

