@NAMES{@NAMES_MALE, @NAMES_FEMALE}=();


sub debug { $DEBUG or return 1; print STDERR "@_" }

sub is_name {
   my $arg = _basic_word_validation($_[0]) or debug("Not a basic word.") and return;
   exists $NAMES{lc($arg)} ? $arg : 0   
}

sub _basic_word_validation {
   ($_[0] and $_[0]=~/^[a-zA-Z]+$/) or return;
   $_[0];
}

1;




