# Used only wrt BID encoding

sub _decode_mant {
  my $val = shift;
  my $ret = '';
  for my $i(0 .. 33) {
    my $count = 0;
    if($val > 0) {
      while($val >= $Math::Decimal128::bid_decode{$i}) {
        $val -= $Math::Decimal128::bid_decode{$i};
        $count++;
      }
    }
    $ret .= $count;
  }
  return $ret;
}

