sub d128_fmt {
  my $d128 = MEtoD128('1234567890123456789012345678901234', 0);
  # DPD: 2608134B9C1E28E56F3C127177823534
  # BID: 30403CDE6FFF9732DE825CD07E96AFF2
  return 'DPD' if d128_bytes($d128) =~ /534$/i;
  return 'BID' if d128_bytes($d128) =~ /FF2$/i;
  return 'Unknown';
}

#######################################################################
