# -----------------------------------------------------------------------------
# import
#
sub import
{
  my $pkg = shift;
  my ($callerpkg) = caller;
  my %exp =
  (
    '&unijp' => \&unijp,
  );
  my @na;
  my @add = (grep{$_ eq ':all'} @_) ? keys %exp : ();
  foreach(@_, @add)
  {
    $_ eq 'PurePerl' and $PurePerl=1, next;
    if( $exp{$_} || $exp{'&'.$_} )
    {
      no strict 'refs';
      (my $name = $_) =~ s/^\W//;
      my $obj = $exp{$_} || $exp{'&'.$_};
      *{$callerpkg.'::'.$name} = $obj;
    }elsif( $_ eq 'no_I18N_Japanese' )
    {
      $^H &= ~0x0f00_0000;
      package Unicode::Japanese::PurePerl;
      $^H &= ~0x0f00_0000;
      package Unicode::Japanese;
      next;
    }
    push(@na,$_);
  }
  if( @na )
  {
    #use Carp;
    #croak("invalid parameter (".join(',',@na).")");
  }
}

