        $str =~ tr/\x21-\x7e/\xa1-\xfe/;
        if($esc =~ m/^$RE{JIS_0208}/)
	  {
	    $str =~ s/($RE{EUC_C})/
	      $J2S[unpack('n', $1)] or $this->_j2s3($1)
