def node_type %n {
	      foreach (%n) {
	      if ( . = self::* ) { # XPath trick to check if . is an element
	      echo 'element';
	      } elsif ( . = ../@* ) { # XPath trick to check if . is an attribute
	      echo 'attribute';
	      } elsif ( . = ../processing-instruction() ) {
	      echo 'pi';
              } elsif ( . = ../text() ) {
	      echo 'text';
              } elsif ( . = ../comment() ) {
              echo 'comment'
	      } else { # well, this should not happen, but anyway, ...
              echo 'unknown-type';
              }
              }
	      }

