$it= Triceps::IndexType->newPerlSorted("b_c", undef, 
sub {
	my $res = ($_[0]->get("b") <=> $_[1]->get("b")
		|| $_[0]->get("c") <=> $_[1]->get("c"));
	return $res;
}
);

