	$unit->call($rowop);
	$unit->drainFrame();
}

while ($rowop = &readRowop()) { # reads with some user-defined function
	$unit->schedule($rowop);
	$unit->drainFrame();
}

while ($rowop = &readRowop()) { # reads with some user-defined function
	$unit->call($rowop);
	while (!$unit->empty()) {
		$unit->callNext();
	}
}

while (1) {
	if (!$unit->empty()) {
		$rowop = &readRowopNoWait();
		if ($rowop) {
			$unit->call($rowop);
		} else {
			$unit->callNext();
		}
	} else {
		$rowop = &readRowop();
		last if (!$rowop); # no more input
		$unit->call($rowop);
	}
}

