package MyTemplate;

sub new # ($class, $unit, $name, $rowType, ...)
{
	my $class = shift;
	my $unit = shift;
	my $name = shift;
	my $rowType = shift;
	my $self = {};

	...

	$self->{unit} = $unit;
	$self->{inputLabel} = $unit->makeLabel($rowType, $name . ".in",
		sub { ... }, sub { ... }, $self);

	...

	bless $self, $class;
	return $self;
}

package MyTemplate;

sub new # ($class, $unit, $name, $rowType, ...)
{
	my $class = shift;
	my $unit = shift;
	my $name = shift;
	my $rowType = shift;
	my $self = {};

	...

	$self->{unit} = $unit;
	$self->{inputLabel} = $unit->makeLabel($rowType, $name . ".in",
		\&clear, \&handle, $self);

	...

	bless $self, $class;
	return $self;
