my $rtCount = Triceps::RowType->new(
	address => "string",
	count => "int32",
);

my $ttCount = Triceps::TableType->new($rtCount)
	->addSubIndex("byAddress", 
		Triceps::IndexType->newHashed(key => [ "address" ])
	)
