sect2.pre:
Triceps::App::build "a1", sub {
	Triceps::App::globalNexus(
		name => "types",
		rowTypes => [
			rt1 => $rt1,
		],
	);
	Triceps::Triead::start(
		app => $Triceps::App::name,
		thread => "t1",
		main => sub {
			my $opts = {};
			&Triceps::Opt::parse("t1 main", $opts, {@Triceps::Triead::opts}, @_);
			my $to = $opts->{owner};
			$to->importNexus(
				from => "global/types",
				import => "writer", # if importing just for the types, use "writer"!
			);
			$to->readyReady();
		},
	);
};
